import React, { useState, useEffect } from 'react';
import { Button, RadioButton } from 'react-native-paper';
import { 
  View, 
  KeyboardAvoidingView, 
  Platform, 
  Text, 
  TouchableOpacity, 
  StyleSheet,
  FlatList } from 'react-native';
import { useNavigation } from '@react-navigation/core';

export function Home() {
  const [mod, setMod] = useState('1');

  const buttons = [
    { 
      name: 'ModOnde', 
      id: '1'
    },
    { 
      name: 'ModTwo', 
      id: '2'
    },
  ];

  const navigation = useNavigation();

  function setHandleMod(modelo) {
    setMod(modelo)
  };

  function plus() { 
    if (mod == 1) {
      navigation.navigate('PMod');
    }

    else {
      navigation.navigate('SMod');
    }
  }

  return (
    <KeyboardAvoidingView enabled={Platform.OS === 'ios'} behavior="padding" style={styles.container}>

      <View style={styles.form}>
        
        <View style={styles.user}>
          <Text style={styles.txt}>Olá,</Text>
          <Text style={styles.txtuser}>User</Text>
        </View>

        <View style={styles.text}>
          <Text style={styles.one}>Em qual modelo</Text>
          <Text style={styles.two}>Você quer fazer o inventario?</Text>
        </View>

        <View style={styles.formmod}>

        <FlatList
          keyExtrector={item => item.id}
          data={buttons}
          horizontal
          renderItem={({ item }) => (
            
          <TouchableOpacity 
          onPress={() => {setHandleMod(item.id)}}
          style={styles.buttonOne}>
          <Text style={styles.buttonText}>M1</Text>
          </TouchableOpacity>

          )}
        />

        </View>
        

        <View style={styles.listaProdutos}>
          <Text style={styles.total}>Total de Produtos</Text>
          <Text style={styles.num}>X</Text>
        </View>

        <View style={styles.prodlist}>
          <View>
            <Text style={styles.list}>Produtos listados</Text>
          </View>

          
            <Button onPress={plus} style={styles.add}>
             <Text style={styles.buttonplustext}>+</Text>
            </Button>
        </View>

      </View>
    </KeyboardAvoidingView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },

  form: {
    width: 'auto',
    height: 'auto',
    
  },

  user: {
      flexDirection: 'column',
      justifyContent: 'flex-start',
      alignItems: 'flex-start',
      alignSelf: 'flex-start',
      alignContent: 'space-around',
      width: 'auto',
      height: 'auto',
      marginTop: '40%',
      marginLeft: '10%',
  },

  txt: {
    fontSize: 30,
  },

  txtuser: {
    fontSize: 30,
    fontWeight: 'bold',
  },

  text: {
    flexDirection: 'column',
    direction: 'ltr',
    justifyContent: 'center',
    alignItems: 'flex-start',
    alignSelf: 'flex-start',
    alignContent: 'flex-start',
    width: 'auto',
    height: 'auto',
    marginTop: '10%',
    marginLeft: '10%',
  },

  one: {
    fontWeight: 'bold',
    width: 'auto',
    height: 'auto',
  },

  two: {
    width: 'auto',
    height: 'auto',
  },

  listaProdutos: {
    flexDirection: 'column',
      justifyContent: 'flex-start',
      alignItems: 'center',
      alignSelf: 'flex-start',
      alignContent: 'space-around',
      width: 'auto',
      height: 'auto',
      marginLeft: '10%',
      marginBottom: '10%',
  },

  total: {
    fontWeight: 'bold',
    fontSize: 18
  },

  num: {
    fontWeight: 'bold',
    fontSize: 26,
  },
  
  list: {
    fontSize: 15,
    marginLeft: '24%',
    alignItems: 'stretch',
  },

  formmod: {
   flexDirection: 'row',
   width: 'auto',
   height: 'auto',
  },

  buttonOne: {
    height: '28%',
    width: '35%',
    backgroundColor: '#E8E8E8',
    justifyContent: 'center',
    alignItems: 'center',
    borderRadius: 13,
    marginTop: '5%',
    marginLeft: '13%',
    borderColor: '#ff0000',
  },

  buttonTwo: {
    height: '28%',
    width: '35%',
    backgroundColor: '#E8E8E8',
    justifyContent: 'center',
    alignItems: 'center',
    borderRadius: 13,
    marginTop: '5%',
    marginLeft: '3%',
  },

  buttonText: {
    color: '#606060'
  },

  prodlist: {
      flexDirection: 'row',
      flex: 1, 
      justifyContent: 'space-between',
  },

  add: {
    backgroundColor: '#4B7DFE',
    justifyContent: 'center',
    alignItems: 'center',
    borderRadius: 8,
    marginRight: '5%',
    width: 'auto',
    height: 'auto',
  },

  buttonPlus: {
    
  },
  
  buttonplustext: {
    fontSize: 25,
    justifyContent: 'center',
    alignItems: 'center',
    color: '#fff',
  },
  
});

export default Home;
